<?php
// Decode base64 for the JSON
$jsonpostrequestbase64 = "";
$jsonpostrequest = base64_decode($jsonpostrequestbase64);
// API URL
$url = "https://dotnetfiddle.net/Home/Run";    
$content = $jsonpostrequest;
// Create a new cURL resource
$ch = curl_init($url);
// Attach encoded JSON string to the POST fields
curl_setopt($ch, CURLOPT_POSTFIELDS, $content);
// Set the content type to application/json
curl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type:application/json'));
// Return response instead of outputting
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
// Execute the POST request
$result = curl_exec($ch);
// Close cURL resource
curl_close($ch);
// Parse output
$parsedresponse = json_decode($result, true);
echo $parsedresponse['ConsoleOutput'];
?>